<template>
	<div>
		<!-- 头部 -->
    <top-head
      @go-back="goBack()"
      :show_back="true"
      :show_border="false"
    >
      <div slot="left"></div>
      <div slot="main">{{title}}</div>
      <div slot="right">
        <span class="right-span" v-if="login_type=='password'" @click="switchLoginType('captcha')">登录</span>
        <span class="right-span" v-if="login_type=='captcha'"  @click="switchLoginType('password')">密码登录</span>
      </div>
    </top-head>
    <!-- 头部 -->

    <div class="login-wrap">
    	<div class="top-blank"></div>

      <img v-if="macross" class="img-banner" src="~@/assets/images/register-banner.jpg">

      <div class="input-content-wrap" :class="flip?'flip':''">
        <!-- 密码登录 -->
        <template v-if="login_type=='password'">
    	    <ul class="info-wrap">
    	    	<li class="info-cell">
    	    		<div class="info-cell-content">
    		    		<label class="info-cell-label">账号</label>
    		    		<div class="info-cell-content-wrap">
    		    			<input required ref="phone" class="info-cell-input" autocomplete="off" type="text" name="phone" @click="clickInput('phone')" @blur="inputBlurValidate('phone')" v-model="info.phone" placeholder="请输入你的手机号" />
    		    		  <span class="clear-input-btn iconfont icon-close2" style="left:auto;right:0.1rem;" @click="clearInputValue('phone')"></span>
                </div>
    	    		</div>
    	    		<p v-if="!validation.phone" class="info-error">手机号格式不正确</p>
    	    	</li>
    	    	<li class="info-cell">
    	    		<div class="info-cell-content">
    		    		<label class="info-cell-label">密码</label>
    		    		<div class="info-cell-content-wrap">
                  <template v-if="!show_password">
                    <input required ref="password6_12" class="info-cell-input" style="width:80%;" autocomplete="off" type="password" name="password6_12" @click="clickInput('password6_12')" @blur="inputBlurValidate('password6_12')" v-model="info.password6_12" placeholder="请输入登录密码" />
                    <span class="clear-input-btn iconfont icon-close2" style="left:auto;right:1rem;" @click="clearInputValue('password6_12')"></span>
                    <span class="iconfont icon-close-eye"  @click="showPassword"></span>
                    <!-- <span class="right-text" @click="goToResetPassword">忘记密码</span> -->
                  </template>
    		    			<template v-else>
    		    				<input required ref="password6_12" class="info-cell-input" style="width:80%;" autocomplete="off" type="text" name="password6_12" @click="clickInput('password6_12')" @blur="inputBlurValidate('password6_12')" v-model="info.password6_12" placeholder="请输入登录密码" />
    		    				<span class="clear-input-btn iconfont icon-close2" style="left:auto;right:1rem;" @click="clearInputValue('password6_12')"></span>
                    <span class="iconfont icon-yanjing" @click="showPassword"></span>
                    <!-- <span class="right-text" @click="goToResetPassword">忘记密码</span> -->
    		    			</template>
    		    		</div>
    	    		</div>
    	    		<p v-if="!validation.password6_12" class="info-error">密码格式不正确</p>
    	    	</li>
    	    </ul>

          <div class="bottom-btn-wrap">
            <a class="login-btn" :class="(pass_validation&&!login_btn_disable)?'active':''" href="javascript:void(0);" @click="login">登录</a>
          </div>

          <div class="other-wrap">
            <span></span>
            <!-- <a class="other-btn" href="javascript:void(0);" @click="switchLoginType('captcha')">短信验证码登录</a> -->
            <a class="other-btn big" href="javascript:void(0);" @click="goToResetPassword">忘记密码</a>
            <!-- <a class="other-btn" href="javascript:void(0);" @click="goToResetPassword">忘记密码？</a> -->
          </div>
        </template>
        <!-- 密码登录 -->

        <!-- 验证码登录 -->
        <template v-if="login_type=='captcha'">
          <ul class="info-wrap">
            <li class="info-cell">
              <div class="info-cell-content">
                <div class="info-cell-content-wrap">
                  <input required ref="phone" class="info-cell-input" autocomplete="off" type="text" name="phone" @click="clickInput('phone')" @blur="inputBlurValidate('phone')" v-model="info.phone" placeholder="请输入你的手机号" />
                  <span class="clear-input-btn iconfont icon-close2" style="left:auto;right:0.1rem;" @click="clearInputValue('phone')"></span>
                </div>
              </div>
              <p v-if="!validation.phone" class="info-error long">手机号格式不正确</p>
            </li>

            <li class="info-cell no-border">
              <div class="info-cell-content">
                <div class="info-cell-content-wrap">
                  <input required ref="captcha" autocomplete="off" class="info-cell-input captcha" @click="clickInput('captcha')" @blur="inputBlurValidate('captcha')" name="captcha" type="text" v-model="info.captcha" placeholder="短信验证码" />
                  <span class="clear-input-btn iconfont icon-close2" style="left:3.6rem;" @click="clearInputValue('captcha')"></span>
                  <div class="captcha-btn" :class="root_data.has_send_captcha?'sending':''" @click="clickSendCaptcha">{{root_data.captcha_text}}</div>
                </div>
              </div>
              <p v-if="!validation.captcha" class="info-error long">验证码格式不正确</p>
            </li>

          </ul>

          <div class="bottom-btn-wrap">
            <a class="login-btn" :class="(pass_validation&&!login_btn_disable)?'active':''" href="javascript:void(0);" @click="login">登录</a>
          </div>

          <div class="other-wrap">
            <!-- <a class="other-btn" href="javascript:void(0);" @click="switchLoginType('password')">账号密码登录</a> -->
            <!-- <a class="other-btn" href="javascript:void(0);" @click="goToRegister">新用户注册</a> -->
            <!-- <a class="other-btn" href="javascript:void(0);" @click="goToResetPassword">忘记密码？</a> -->
            <p>未注册用户登录后将自动注册，且表示同意<a class="other-btn" @click="show_register_contract=!show_register_contract">《黄金象用户注册协议》</a></p>
          </div>

          <!-- 合作伙伴 -->
          <div class="partner-wrap">
            <h4 class="partner-title">合作伙伴</h4>
            <img class="partner-img" src="data:image/png;base64,">
          </div>
          <!-- 合作伙伴 -->
        </template>
        <!-- 验证码登录 -->
	    </div>

    </div>


    <!-- 黄金象用户注册协议 -->
      <Popup
          v-model="show_register_contract"
          position="right" 
          class="pop-modal">
        <a href="javascript:void(0);" class="iconfont icon-back" @click="show_register_contract=!show_register_contract"></a>
        <div class="title">黄金象用户注册协议</div>
        <div class="content">
          <div class="pop-content-wrap" v-html= "contract.body"></div>
        </div>
      </Popup>
    <!-- 黄金象用户注册协议 -->

	</div>
</template>

<script>
import appconfig from "@/config/appconfig.js";
import topHead from "@/views/components/TopHead.vue";
import constants from "@/assets/js/constants.js";
import store from "@/vuex/index.js";

import { Toast, MessageBox, Popup } from "mint-ui";

export default {
  components: {
    topHead: topHead,
    Popup:Popup,
  },
  data: function() {
    return {
      login_type:"captcha",//password,captcha
      show_password: false,
      captcha_image_base64:"",
      access_token:"",
      info: {
        phone: "",
        password6_12: "",
        // captcha_image:"",
        captcha:"",
      },
      validation: {
        phone: true,
        password6_12: true,
        // captcha_image:true,
        captcha:true,
      },
      pass_validation: false,

      root_data:{
        has_send_captcha:false,
        captcha_text:"获取验证码",
      },

      registered:false,
      
      flip:false,

      contract:{
        title:"",
        body:"",
      },
      show_register_contract:false,

      login_btn_disable:false,

      route_from:{},
      // referrer:"",
    };
  },
  computed: {
    macross() {
      return this.$store.getters.getMacross;
    },
    resume_stamp() {
      return this.$store.getters.getResumeStamp;
    },
    title(){
      return this.login_type=="captcha"?"登录":"密码登录";
    },
    pfc_code() {
      return this.$store.getters.getPfcCode||"app未知渠道";
    },
  },
  watch: {
    info: {
      handler: function(nval, oval) {
        var me = this,
          success = true;

        if(this.login_type == "password"){
          ["phone","password6_12"].forEach(function(key){
            var res = me.validate(key);
            // me.validation[key] = res;
            if(!res){
              success = false;
            }
          });
        }else if(this.login_type == "captcha"){
          ["phone","captcha"].forEach(function(key){
            var res = me.validate(key);
            // me.validation[key] = res;
            if(!res){
              success = false;
            }
          });
        }
        this.pass_validation = success;
      },
      deep: true
    },
    resume_stamp:function(nval,oval){//app  reume
      utils.trackEvent("exposure_quickLogin");
    },
    "info.phone":function(nval,oval){
      var me = this;

      ///////////////判断是否是新用户还是老用户////////////////
      if(this.validate("phone")){
        this.simulateLogin()
        .then(function(e){
          if(e.errorCode == "user_notexist"){//未注册
            me.registered = false;
          }else if(e.errorCode == "userinfo_error"){//已经注册
            me.registered = true;
          }
        })
      }
      ///////////////判断是否是新用户还是老用户////////////////
    },
  },
  beforeRouteEnter(to, from, next) {
    next(vm => {
      // vm.referrer = from.fullPath;
      vm.route_from = from;
      // 通过 `vm` 访问组件实例
      vm.init();
      utils.trackEvent("exposure_quickLogin");
    })
  },
  methods: {
    init:function(){
      var me = this;

      this.login_type = "captcha";//password,captcha
      this.show_password = false;
      this.pass_validation = false;
      this.flip = false;
      this.login_btn_disable = false;

      this.info = _.assign({},{
        phone: "",
        password6_12: "",
        // captcha_image:"",
        captcha:"",
      });
      this.validation = _.assign({},{
        phone: true,
        password6_12: true,
        // captcha_image:true,
        captcha:true,
      });

      this.getAccessToken(function(){
        // me.getCaptchaImage(me.access_token);
      });
      this.getContract();
    },
    goBack: function() {
      if(this.route_from.meta.need_login){
        this.$router.replace({path: "/"});
      }else{
        this.$router.goBack();
      }
    },
    backTorReferrer:function(){
      this.$router.goBack();
      // if(this.route_from.meta.no_history){
      //   // this.$router.replace({path: "/my" });
      //   this.$router.go(-2);
      //   // this.$router.goBack();
      // }else{
      //   this.$router.goBack();
      //   // this.$router.replace({path: `${this.route_from.fullPath}` });
      // }
      // this.$router.go(-1);
    },

    getContract:function(){
      var me = this;

      utils.API.cmsservice_contract_register({
        method:"get",
      })
      .then(function(ret) {
        let contract = "";

        if(ret.model&&ret.model.length){
          contract = ret.model[0];

          var reg = /\"+?\/(.+)((\.jpg)|(\.png))\"/gi;
          contract.body = contract.body.replace(reg,"\""+appconfig.cmsbaseurl+"/"+"$1$2"+"\"");
          me.contract = _.assign({},contract);
        }
      })
      .catch(function(ret) {
        !ret.__servererror__ && Toast(ret.errorMessage || "未知错误！");
      });
    },
    getAccessToken:function(cb){
      var me = this;

      utils.API.access_token({
        data:{}
      })
      .then(function (ret) {
        me.access_token = ret.model.data.access_token;
        cb&&cb();
      })
      .catch(function(ret){
        !ret.__servererror__ && Toast(ret.errorMessage||"未知错误！");
        // me.root_data.has_send_captcha = false;
      })
    },
    // getCaptchaImage:function(access_token){
    //   var me = this;

    //   this.validation.captcha_image = true;

    //   utils.API.captcha_image({
    //     data:{
    //       token:access_token,
    //     }
    //   })
    //   .then(function (ret) {
    //     me.captcha_image_base64 = ret.model.data.base64;
    //   })
    //   .catch(function(ret){
    //     !ret.__servererror__ && Toast(ret.errorMessage||"未知错误！");
    //   })
    // },

    simulateLogin:function(){
      var me = this;

      return new Promise(function(resolve, reject) {
        utils.API.user_signin({
          data: {
            type: "password",
            name: _.trim(me.info.phone),
            password:"",
          }
        })
        .then(function(ret) {
          // resolve(ret.model);
          reject(ret)
        })
        .catch(function(e) {
          // reject(e)
          resolve(e);
          // !e.__servererror__ && Toast(e.errorMessage || "未知错误！");
        });
      })  
    },

    clickSendCaptcha:function(){
      var me = this,
          success = true;

      ["phone"].forEach(function(key){
        var res = me.validate(key);
        me.validation[key] = res;
        if(!res){
          success = false;
        }
      });

      if(!success){//表单输入格式验证
        return;
      }

      this.sendCaptcha(function(ret){
        if(ret.data){
          // Toast("验证码已发送！");
          me.registered = ret.data.registered;

          if(ret.data.registered){//已注册
            utils.trackEvent("click_quickLogin_smsButton_success",{
              user_register_status:"1",
              phone:_.trim(me.info.phone),
            });
          }else{
            utils.trackEvent("click_quickLogin_smsButton_success",{
              user_register_status:"0",
              phone:_.trim(me.info.phone),
            });
          }
        }else{
          if(me.pfc_code == "111111" || me.pfc_code == "222222"){//开发和测试环境发送错误可以通过
            // me.registered = false;
          }
        }
      })
    }.after(function() {
      utils.trackEvent("click_quickLogin_smsButton",{
        phone:_.trim(this.info.phone),
      });
    }),


    sendCaptcha:function(cb){
      var me = this;

      if(this.root_data.has_send_captcha){//倒计时中
        return;
      }

      // this.root_data.has_send_captcha = false;


      me.root_data.has_send_captcha = true;
      this.info.captcha = "";
      utils.countDown({//倒计时
        seconds:59,//倒计时总时间
        beforeInterval:function(counter){//beforeInterval
          me.root_data.captcha_text = '已发送 '+counter;
        },
        readingSeconds:function(counter){//读秒中callback
          me.root_data.captcha_text = '已发送 '+counter;
        },
        end:function(){//倒计时完成后callback
          me.root_data.captcha_text = "获取验证码";
          me.root_data.has_send_captcha = false;
        }
      });

      // 发送验证码
      utils.API.user_userbind({
        headers:{
          "api-version" : "1.15.0",
        },
        data:{
          "token":me.access_token,
          "type":"mobile",
          "value":_.trim(me.info.phone),
          // "captcha_code":_.trim(me.info.captcha_image),
        }
      })
      .then(function (ret) {
        
        cb&&cb(ret.model);
      })
      .catch(function(ret){
        !ret.__servererror__ && Toast(ret.errorMessage||"未知错误！");
        me.root_data.has_send_captcha = false;
        utils.resetCountDown(function(){
          me.root_data.captcha_text = "获取验证码";
        });
        if(me.pfc_code == "111111" || me.pfc_code == "222222"){//开发和测试环境发送错误可以通过
          cb&&cb(ret);
        }
      })
    },




    showPassword: function() {
      this.show_password = !this.show_password;
    },
    validate: function(key) {
      var success = new RegExp(constants.FORM_REG[key], "g").test(
        _.trim(this.info[key])
      );
      return success;
    },
    inputBlurValidate: function(key) {
      return;
      //表单输入框在blur时验证格式
      this.validation[key] = this.validate(key);
    },
    clickInput: function(key) {
      this.validation[key] = true;
    },
    clearInputValue:function(key){
      this.info[key] = "";
      this.$refs[key].focus();
      this.validation[key] = true;
    },


    checkValidate:function(){
      var me = this,
          success = true;

        if(this.login_type == "password"){
          ["phone","password6_12"].forEach(function(key){
            var res = me.validate(key);
            me.validation[key] = res;
            if(!res){
              success = false;
            }
          });
        }else if(this.login_type == "captcha"){
          ["phone","captcha"].forEach(function(key){
            var res = me.validate(key);
            me.validation[key] = res;
            if(!res){
              success = false;
            }
          });
        }
        return success;
    },

    checkoutCaptcha:function(cb){
      var me = this;

      utils.API.validate_bind({
        data:{
          "token":me.access_token,
          "type":"mobile",
          "value":_.trim(me.info.phone),
          "code":_.trim(me.info.captcha),
        }
      })
      .then(function (ret) {
        cb&&cb();
      })
      .catch(function(ret){
        me.login_btn_disable = false;
        !ret.__servererror__ && Toast(ret.errorMessage||"未知错误！");
      })
    },

    login: function() {
      var _this = this,
          send_data = {};

      if(this.login_btn_disable){
        return;
      }

      if(!this.pass_validation){
        return;
      }

      if (!this.checkValidate()) {
        return;
      }

      switch(this.login_type){
        case "password":
          send_data = {
            type: "password",
            name: _.trim(_this.info.phone),
            password: _.trim(_this.info.password6_12),
          };
          break;
        case "captcha":
          send_data = {
            token:_this.access_token,
            type: "smslogin",
            name: _.trim(_this.info.phone),
            password: _.trim(_this.info.captcha),
          };
          break;
      }


      this.login_btn_disable = true;

      if(this.login_type == "password"){
        this.userSignin(send_data,function(){//登录
          utils.trackEvent("click_passwordlogin_success",{
            user_register_status:"1",
          });
          setTimeout(function(){
            _this.backTorReferrer();
          },200)
        });
      }else{
        this.checkoutCaptcha(()=>{
          if(this.registered){//已有账号
            this.userSignin(send_data,function(){//登录
              utils.trackEvent("click_quickLogin_loginButton_success",{
                user_register_status:"1",
              });
              setTimeout(function(){
                _this.backTorReferrer();
              },200)
            })
          }else{
            this.userSignup(function(){//注册
              setTimeout(function(){
                _this.backTorReferrer();
              },200)
            })
          }
        })
      }
    }.after(function() {
      switch(this.login_type){
        case "password":
          utils.trackEvent("click_passwordlogin");
          break;
        case "captcha":
          utils.trackEvent("click_quickLogin_loginButton");
          break;
      }
    }),

    //登录
    userSignin:function(send_data,cb){
      var me = this;

      utils.API.user_signin({
        headers:{
          "api-version" : "1.15.0",
        },
        data:send_data,
      })
      .then(function(ret) {
        utils.storageClear();

        utils.storageSet("mall_token", ret.model.data.session.sid);
        utils.storageSet("mall_uid", ret.model.data.session.uid);
        utils.storageSet("mobile", me.info.phone);

        utils.syncSetNativeStorage({
          "mall_token":ret.model.data.session.sid,
          "mall_uid":ret.model.data.session.uid,
          "mobile":me.info.phone,
        });
        
        me.$store.commit("setMallToken", ret.model.data.session.sid);
        me.$store.commit('setHasShowHomeWelcome',true);
        
        // me.login_btn_disable = false;
               
        cb&&cb();
      })
      .catch(function(e) {
        me.login_btn_disable = false;
        !e.__servererror__ && Toast(e.errorMessage || "未知错误！");
      });
    },

    //商城开户
    userSignup:function(cb){
      var me = this;
      utils.API.user_signup({
        data:{
          "token":me.access_token,
          "name":_.trim(me.info.phone),
          "password":0,
          "field" : [                 //其他信息
            {
            "value" : me.pfc_code||"",//传递的值
            "id" : "6"                //其他信息的ID,6代表source_code
            },
          ],
        }
      })
      .then(function (ret) {
        utils.storageClear();

        utils.storageSet("mall_token", ret.model.data.session.sid);
        utils.storageSet("mall_uid", ret.model.data.session.uid);
        utils.storageSet("mobile", me.info.phone);

        utils.syncSetNativeStorage({
          "mall_token":ret.model.data.session.sid,
          "mall_uid":ret.model.data.session.uid,
          "mobile":me.info.phone,
        });
        
        me.$store.commit("setMallToken", ret.model.data.session.sid);
        me.$store.commit('setHasShowHomeWelcome',true);

        // me.login_btn_disable = false;
        utils.trackEvent("click_quickLogin_loginButton_success",{
          user_register_status:"0",
        }); 
        cb&&cb();
      })
      .catch(function(ret){
        me.login_btn_disable = false;
        !ret.__servererror__ && Toast(ret.errorMessage||"未知错误！");
      });
    },

    goToRegister: function() {
      this.$router.push({ path: "/register" });
    }.after(function() {
      utils.trackEvent("click_signin_signupButton");
    }),

    goToResetPassword: function(index) {
      this.$router.push({ path: "/reset_login_password" });
    }.after(function() {
      utils.trackEvent("click_signin_forgetPasswordButton");
    }),


    switchLoginType:function(type){
      var me = this;
      this.flip = true;
      
      setTimeout(()=> {
        this.login_type = type;
      },250);
      setTimeout(()=> {
        this.flip = false;
      },500);
    },
  }
};
</script>

<style lang="less" scoped>
@import './Login.less';
</style>