<template>
	<div>
    <!-- 头部 -->
    <top-head
      @go-back="goBack()"
      :show_back="true"
    >
      <div slot="left"></div>
      <div slot="main">客户服务</div>
      <div slot="right"></div>
    </top-head>
    <!-- 头部 -->
		
		<div class="guest-service">
			<div class="top-blank"></div>

			<div class="service-top">
				<p class="service-time">联系客服(工作日9：00-12:00；13:00-17:00)</p>
				<a class="service-phone" @click="clickGuestPhone" :href="'tel:'+guest_phone">{{guest_phone}}</a>
			</div>

			<div class="help-center-wrap">
				<p class="help-center-title">帮助中心</p>

				<ul class="help-tag-wrap">
					<li class="help-tag" :class="tag.is_select==true?'active':''" v-for="(tag,index) in tags" >
						<div class="help-tag-title" :style="'left:'+index*20+'%;'" @click="switchTag(tag,index)">{{tag.name}}</div>
						<ul class="qa-list-wrap">
							<li class="qa-list" :class="qa.show_qa_list?'show-qa-list':''" v-for="qa in tags[index].qa_list">
								<div class="qa-list-title" @click="qa.show_qa_list=!qa.show_qa_list">{{qa.title}}</div>
								<div class="qa-list-body" v-html="qa.body"></div>
							</li>
						</ul>	
					</li>
				</ul>

				<div class="bottom-wrap">
					<p>如您在非客服工作时间需要帮助，可在黄金象官方微信</p>
					<p>公众号留言，我们将尽快与您联系。</p>

					<div class="swip-wrap">
						<img @click="follow" class="QR-code" src="data:image/jpeg;base64,">
						<h4 class="swip-app">扫描(或点击)公众号二维码：黄金象</h4>
					</div>
				</div>
				
				
			</div>

		</div>
	</div>
</template>



<script>

import appconfig from "@/config/appconfig.js";
import topHead from "@/views/components/TopHead.vue";

import {
	Toast,
	MessageBox
} from 'mint-ui';

// utils.extendApi(API);//把API扩充到utils方法里边

export default {
	components:{
		topHead:topHead,
	},
	data(){
		return {
			tags:[],
			// qa_list:[],
			// cache:"",
			// version:appconfig.VERSION,
		}
	},
	computed:{
		guest_phone(){
      return this.$store.getters.getGuestPhone;
    },
	},
	// beforeRouteEnter (to, from, next) {
	//   next(vm => {
	//     // 通过 `vm` 访问组件实例
	//   })
	// },
	mounted(){
		this.init();
	},
	methods:{
		init:function(){
			var me = this;
			this.getTags(function(tags){
				me.getQaList(tags[0].tid);
			});
		},
		getTags:function(cb){
			var me = this;

			utils.API.cmsservice_tags_qa({
        method:"get",
      })
      .then(function(ret) {
      	// console.log(ret)
      	ret.model.forEach(function(item,index){
      		item.qa_list = [];
      		if(index == 0){
      			item.is_select = true;
      		}else{
      			item.is_select = false;
      		}
      	});

      	me.tags = ret.model;
      	cb&&cb(ret.model);
        // me.user_info = _.assign({}, me.user_info, ret.model.data);
      })
      .catch(function(ret) {
        !ret.__servererror__ && Toast(ret.errorMessage || "未知错误！");
      });
		},
		getQaList:function(tid){
			var me = this;

			utils.API.cmsservice_qa_list({
        method:"get",
				data:{
					filter: tid
				}
      })
      .then(function(ret) {
      	var tag = _.find(me.tags,function(o){return o.tid == tid});
      	
      	if(!_.isEmpty(tag)){
      		ret.model.forEach(function(item){
      			item.show_qa_list = false;
      		})
      		tag.qa_list  = ret.model;
      	}
      	
      })
      .catch(function(ret) {
        !ret.__servererror__ && Toast(ret.errorMessage || "未知错误！");
      });
		},
		switchTag:function(tag){
			this.tags.forEach(function(item){
				if(tag.tid == item.tid){
					item.is_select = true;
					item.qa_list.show_qa_list = true;
				}else{
					item.is_select = false;
					item.qa_list.show_qa_list = false;
				}
			});

			if(tag.qa_list.length <= 0){
				this.getQaList(tag.tid);
			}
		},
		goBack:function(){
			this.$router.goBack();
		},
		follow:function(){
			utils.follow();
		},
		clickGuestPhone:function(){
			utils.trackEvent('click_my_MyService_phone');
		},
	}
};
</script>


